const _defaultListener = () => {}

let _capturing = false
let _listener = () => {}

const setListener = (fn) => {
  _capturing = Boolean(fn)
  _listener = fn || _defaultListener
}

const getListener = () => {
  return _listener
}

const addToQueue = (cqn) => {
  _listener(cqn)

  return _capturing
}

module.exports = {
  setListener,
  getListener,
  addToQueue
}
